mapper.select:
select 

from news
where type = #{type,jdbcType=SMALLINT} and level= #{level,jdbcType=SMALLINT}

mapper.insert:
insert into news (id, title, author, 
  create_time, type, level, 
  html_url, resume, creator
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{author,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{type,jdbcType=SMALLINT}, #{level,jdbcType=SMALLINT}, 
  #{htmlUrl,jdbcType=VARCHAR}, #{resume,jdbcType=VARCHAR}, #{creator,jdbcType=VARCHAR}
  )
